import { REGISTER_REQUEST } from './RegisterTypes'
import { REGISTER_SUCCESS } from './RegisterTypes'
import { REGISTER_FAILURE } from './RegisterTypes'

export const RegisterRequest = (loading) => {
  return {
    type: REGISTER_REQUEST,
    loading,
  }
}
export const RegisterSuccess = (data, loading) => {
  return {
    type: REGISTER_SUCCESS,
    data,
    loading,
  }
}
export const RegisterFailure = (error, loading) => {
  return {
    type: REGISTER_FAILURE,
    error,
    loading,
  };
}
