import { createStore } from 'redux';


const BUY_PASTAS = 'BUY_PASTAS';
const EAT_PASTAS = 'EAT_PASTAS';

const App = () => {


const initialState = {
  pastas: 4
};

//1. Les actions sont des objets simples contenant un type, étant une string, 
  //définissant ce que l'action va faire. 

  const BUY_PASTAS = (weight, index) => {
    return {
      type: 'BUY_PASTAS',
      weight,
      index
    }
  }
  const EAT_PASTAS = (weight, index) => {
    return {
      type: 'EAT_PASTAS',
      weight,
      index
    }
  }

 //2. Le reducer est une fonction pure qui récupère le state précédent et une action, et renvoi le state suivant.
  const reducer = (state = initialState, action) => {
    switch(action.type) {
      case 'EAT_PASTAS':
        return {
          ...state,
          pastas: state.pastas + action.weight
        }
        case 'BUY_PASTAS':
          return {
            ...state,
            pastas: state.pastas + action.weight
          }
          default:
            return state;
          }
