import { RegisterRequest, RegisterFailure, RegisterSuccess } from 'reduxx/Register/RegisterActions';
import Cookies from 'js-cookie'

const fetchNews = (data, url) => {

  return (dispatch) => {
    dispatch(RegisterRequest(true));

    fetch(
      url , {
        method: "post",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(data),
      }
    )
      .then((response) => response.json())
      .then((response) => {
        if (response.status === "error") {
          dispatch(RegisterFailure(response.message, false));
        } else {
          dispatch(RegisterSuccess(response, false));
          console.log(response);
          Cookies.set('token', response.jwt);
          Cookies.set('id', response.user.id);

        }
      })
  }
}

export default RegisterNews;
