import React, { useEffect, useState } from 'react';
import './style.css';
import Cookies from 'js-cookie'



const Profil = () => {

      const [username, setUsername] = React.useState('');
      const [email, setUserEmail] = React.useState('');
      const [password, setUserPassword] = React.useState('');
      const token = Cookies.get('token');
      const [data, SetData] = useState('')


    const profileFetch = () => {

      fetch('http://localhost:1337/users/me', {
          method: "GET",
          headers: {
          "Authorization":`Bearer ${token}`,
          "Content-Type": "application/json"
          },
        })
        .then((response) => response.json())
        .then((data) => {
            SetData(data)
            console.log(data)
          }
        )
    }

    useEffect(() => {
        profileFetch();
    },[])

      return (
            <div className="profil">
              <div className="profilContent">
                <form>
                  <h1>Profil</h1>
                  <h2>Nom : {data.username}</h2>
                  <p> {data.email}</p>
                  <h3> Modifier ton nom </h3>
                  <input type="text" value={username} name="username" onChange={(e)=>setUsername(e.target.value)} required />
                  <p> Modifier ton mot de passe}</p>
                  <input type="email" value={email} onChange={(e)=>setUserEmail(e.target.value)} name="email" />
                  <h3> Modifier ton mot de passe </h3>
                  <input type="email" value={email} onChange={(e)=>setUserEmail(e.target.value)} name="email" />
                </form>
              </div>
            </div>
      )

}


export default Profil;
