import { RegisterRequest, RegisterFailure, RegisterSuccess } from 'reduxx/Register/RegisterActions';
import Cookies from 'js-cookie'

const fetchNews = (data, url, header) => {

  return (dispatch) => {
    dispatch(RegisterRequest(true));

    fetch(
      url , {
        method: "post",
        headers: header,
        body: JSON.stringify(data),
      }
    )
      .then((response) => response.json())
      .then((response) => {
        if (response.status === "error") {
          dispatch(RegisterFailure(response.message, false));
        } else {
          dispatch(RegisterSuccess(response, false));
          console.log(response);
          Cookies.set('token', response.jwt);
          console.log(Cookies.get('token')
          )
        }
      })
  }
}

export default fetchNews;
