import { fetchRequest, fetchFailure, fetchSuccess } from 'redux/fetch/fetchActions';

const fetch = (data) => {

  return (dispatch) => {
    dispatch(fetchRequest(true));

    fetch(
      "http://localhost:1337/auth/local/register" , {
        method: "POST",
        body: JSON.stringify(data),
        headers: {
          "Content-Type": "application/json"
        }}
    )
      .then((response) => response.json())
      .then((response) => {
        if (response.status === "error") {
          dispatch(fetchFailure(response.message, false));
        } else {
          dispatch(fetchSuccess(response, false));
        }
      })
  }
}
