import React from 'react'
import { fetchRequest, fetchFailure, fetchSuccess } from './../../redux/fetch';
import './style.css';


const Login = () => {

  const [identifier, setIdentifier] = React.useState('');
  const [password, setGivenPassword] = React.useState('');

  const data = {
    identifier: identifier,
    password: password
  };


const handleFetch = (e) => {
  e.preventDefault();
  console.log(data)

  return (dispatch) => {
    dispatch(fetchRequest(true));

    fetch(
      "http://localhost:1337/auth/local" , {
        method: "POST",
        body: JSON.stringify(data),
        headers: {
          "Content-Type": "application/json"
        }}
    )
      .then((response) => response.json())
      .then((response) => {
        if (response.status === "error") {
          dispatch(fetchFailure(response.message, false));
        } else {
          dispatch(fetchSuccess(response.articles, false));
        }
      })
  }
}

return (
      <div className="form">
        <form onSubmit={handleFetch}>
          <h1>Login</h1>
          <h2>Identifier (Username or Email)</h2>
          <input type="text" value={identifier} name="identifier" onChange={(e)=>setIdentifier(e.target.value)} required />
          <h2>Password</h2>
          <input type="password" value={password} onChange={(e)=>setGivenPassword(e.target.value)} name="password"/>
          <input type="submit" value="Envoyer"/>
        </form>
      </div>

)

}


export default Login;
