import { fetchRequest, fetchFailure, fetchSuccess } from 'reduxx/fetch/fetchActions';
import Cookies from 'js.cookie.mjs'

const fetchNews = (data, url) => {

  return (dispatch) => {
    dispatch(fetchRequest(true));

    fetch(
      url , {
        method: "post",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(data),
      }
    )
      .then((response) => response.json())
      .then((response) => {
        if (response.status === "error") {
          dispatch(fetchFailure(response.message, false));
        } else {
          dispatch(fetchSuccess(response, false));
          console.log(response);
          Cookies.set('token', response.jwt);
        }
      })
  }
}

export default fetchNews;
