import { FETCH_REQUEST } from './fetchTypes'
import { FETCH_SUCCESS } from './fetchTypes'
import { FETCH_FAILURE } from './fetchTypes'

export const fetchRequest = (loading) => {
  return {
    type: FETCH_REQUEST,
    loading,
  }
}
export const fetchSuccess = (data, loading) => {
  return {
    type: FETCH_SUCCESS,
    data,
    loading,
  }
}
export const fetchFailure = (error, loading) => {
  return {
    type: FETCH_FAILURE,
    error,
    loading,
  };
}
