import React from 'react'
import './style.css'


const Login = () => {

  const [username, setUsername] = React.useState('');
  const [email, setUserEmail] = React.useState('');
  const [password, setUserPassword] = React.useState('');

  const data = {
    username: username,
    email: email,
    password: password
  };

const handleFetch = (e) => {
  e.preventDefault();
  console.log(data)
  fetch('http://localhost:1337/auth/local/register', {
    method: "POST",
    body: JSON.stringify(data),
    headers: {
      "Content-Type": "application/json"
    }
  })
      .then((response) => response.json())
      .then((response) => console.log(response))
      .catch((error) => console.error(error));
      Cookies.set('token', data.jwt);

}

return (
      <div className="form">
        <form onSubmit={handleFetch}>
          <h1>Login</h1>
          <h2>Nom</h2>
          <input type="text" value={username} name="username" onChange={(e)=>setUsername(e.target.value)} required />
          <h2>Email</h2>
          <input type="email" value={email} onChange={(e)=>setUserEmail(e.target.value)} name="email" />
          <h2>Password</h2>
          <input type="paswword" value={password} onChange={(e)=>setUserPassword(e.target.value)} name="password"/>
          <input type="submit" value="Envoyer"/>
        </form>
      </div>

)

}


export default Login;
