import React from 'react'
import './style.css';
import { RegisterFailure, RegisterSuccess } from 'reduxx/Register/RegisterActions';




const Login = () => {

  const [identifier, setIdentifier] = React.useState('');
  const [password, setGivenPassword] = React.useState('');


  const data = {
    identifier: identifier,
    password: password
  };



const handleFetch = (e) => {
  e.preventDefault();

     fetch("http://localhost:1337/auth/local",{
       method: 'GET',
       header: {
        "Content-Type": "application/json"
      },
      body : JSON.stringify(data)
     })
     .then ((response) => response.json())
     .then ((data) => {
        if (data.status === 'error') {
          dispatch(RegisterFailure(data.message, false))
        } else {
          dispatch(RegisterSuccess(response, false));
          console.log(data)
        }
     })
  }


  return (
        <div className="form">
          <form onSubmit={handleFetch}>
            <h1>Login</h1>
            <h2>Identifier (Username or Email)</h2>
            <input type="text" value={identifier} name="identifier" onChange={(e)=>setIdentifier(e.target.value)} required />
            <h2>Password</h2>
            <input type="password" value={password} onChange={(e)=>setGivenPassword(e.target.value)} name="password"/>
            <input type="submit" value="Envoyer"/>
          </form>
        </div>
  )
}

export default Login;
