import React, { useEffect, useState } from 'react';
import './style.css';
import { RegisterFailure, RegisterSuccess } from 'reduxx/Register/RegisterActions';
import Cookies from 'js-cookie'


const Profil = () => {
      const { token } = Cookies.get('token');
      const [data, SetData] = React.useState('')


    const profileFetch = () => {

      fetch('http://localhost:1337/users/me', {
          method: "GET",
          headers: {
          "Authorization":`Bearer ${token}`,
          "Content-Type": "application/json"
          }
        })
        .then((response) => response.json())
        .then((data) => {
          if (data.status === 'error') {
            dispatch(RegisterFailure(data.message, false))
          } else {
            dispatch(RegisterSuccess(data, false));
            SetData(data)
            console.log(data)
          }
        })
    }

    useEffect(() => {
        profileFetch();
    },[])

      return (
            <div className="form">
                <h1>Profil</h1>
                <p> {data}</p>
            </div>
      )

}


export default Profil;
