import React, { useEffect, useState } from 'react';
import Cookies from 'js-cookie';
import handleFetch from 'pages/Register/index'
import './style.css'



const AllProfils = () => {

      const token = Cookies.get('token');
      const [data, SetData] = useState([])


    const AllProfilsFetch = () => {

      fetch('http://localhost:1337/users', {
          method: "GET",
          headers: {
          "Authorization":`Bearer ${token}`,
          "Content-Type": "application/json"
          },
        })
        .then((response) => response.json())
        .then((data) => {
            SetData(data)
            console.log(data)
          }
        )
    }

    useEffect(() => {
        AllProfilsFetch();
    },[])

      return (

              <div className="AllProfilsContent">
                <h2>All Profils</h2>
                  <ul>
                    {data.map((user) => (
                    <div key={user.id} className="Card">
                      <li>
                        <p>{user.username}</p>
                        <button onClick={AllProfilsFetch}>Voir ce profil</button>
                      </li>
                    </div>
                    ))}
                  </ul>
              </div>

      )
}

export default AllProfils
