import React from 'react'
import Cookies from '../../../node_modules/js-cookie'
import './style.css'


const Login = () => {

  const [username, setUserName] = React.useState('');
  const [email, setUserEmail] = React.useState('');
  const [password, setUserPassword] = React.useState('');

  const data = {
    username: username,
    email: email,
    password: password
  };


const handleUserName = (e) => {
  setUserName(e.target.value)
}

const handleUserEmail = (e) => {
  setUserEmail(e.target.value)
}

const handleUserPassword = (e) => {
  setUserPassword(e.target.value)
}


const handleFetch = (e) => {
  e.preventDefault();

  fetch('http://localhost:1337/auth/local/register', {
    method: "POST",
    body: JSON.stringify(data),
    headers: {
      "Content-Type": "application/json"
    }
  })
      .then((response) => response.json())
      .then((response) => console.log(response))
      .catch((error) => console.error(error));
      // Cookies.set('token', token);
}

return (
      <div className="form">
        <form onSubmit={handleFetch}>
          <h1>Login</h1>
          <h2>Nom</h2>
          <input type="text" value={username} name="username" onChange={handleUserName} required />
          <h2>Email</h2>
          <input type="email" value={email} onChange={handleUserEmail} name="email" />
          <h2>Password</h2>
          <input type="paswword" value={password} onChange={handleUserPassword} name="password"/>
          <input type="submit" value="Envoyer"/>
        </form>
      </div>

)

}


export default Login;
