import React from 'react';
import ReactDOM from 'react-dom';
import { createStore } from 'redux';


const App = () => {

const BUY_PASTAS = 'BUY_PASTAS';
const EAT_PASTAS = 'EAT_PASTAS';



  const increment = (weight, index) => {

    const initialState = {
      pastas: 4
    };

//1. Les actions sont des objets simples contenant un type, étant une string, 
  //définissant ce que l'action va faire. 
    return {
      type: 'BUY_PASTAS',
      weight,
      index
    }
  }
  const decrement = (weight, index) => {
    return {
      type: 'EAT_PASTAS',
      weight,
      index
    }
  }

 //2. Le reducer est une fonction pure qui récupère le state précédent et une action, et renvoi le state suivant.
  const counter = (state = 0, action) => {
    switch(action.type) {
      case 'INCREMENT':
        return state + 1;
        case 'DECREMENT':
          return state - 1;
          default:
            return state;
          }
          return state;
        }   

// counter is the name of our future reducer function
        let store = createStore(counter); 
        store.subscribe(() => console.log(store.getState()));

// Pour effectuer une action, on utilise store.dispatch(theActionOrActionCreator).
        store.dispatch(increment());
        store.dispatch(increment());
        store.dispatch(decrement());
        store.dispatch(increment());
        return (
          <p>C'est la vie !</p>
        )
      }

ReactDOM.render(<App />, document.querySelector("#root"));
