import { FETCH_REQUEST, FETCH_SUCCESS, FETCH_FAILURE } from './fetchTypes'

const initialState = {
  loading: false,
  data: [],
  error: ''
}

const fetchReducer = (state = initialState, action) => {
  switch(action.type) {
    case FETCH_REQUEST:
      return {
        ...state,
        loading : action.loading,
      }
      case FETCH_SUCCESS:
        return {
          ...state,
          data : action.data,
          loading : action.loading,
        }
      case FETCH_FAILURE:
        return {
          ...state,
          error : action.error,
          loading : action.loading,
        }
        default:
          return state;
        }
      }  

export default fetchReducer;
