import React from 'react'
import './style.css';
import { store } from 'reduxx/Store'
import fetchNews from 'components/Api'


const Register = () => {
    const [username, setUsername] = React.useState('');
    const [email, setUserEmail] = React.useState('');
    const [password, setUserPassword] = React.useState('');

    const url = "http://localhost:1337/auth/local/register";

    // const header = {
    //   "Content-Type": "application/json"
    // }

    const data = {
      username: username,
      email: email,
      password: password
    };

  const handleFetch = (e) => {
      e.preventDefault();
      // store.dispatch(fetchNews(data, url, header));

      fetch(url,{
        method:'POST',
        headers: {
          "Content-Type": "application/json"
        },
        body : JSON.stringify(data)
      })
      .then((response) => response.json())
      .then((data) => console.log(data))
      }

return (
        <div className="form">
          <form onSubmit={handleFetch}>
            <h1>Register</h1>
            <h2>Nom</h2>
            <input type="text" value={username} name="username" onChange={(e)=>setUsername(e.target.value)} required />
            <h2>Email</h2>
            <input type="email" value={email} onChange={(e)=>setUserEmail(e.target.value)} name="email" />
            <h2>Password</h2>
            <input type="password" value={password} onChange={(e)=>setUserPassword(e.target.value)} name="password"/>
            <input type="submit" value="Envoyer"/>
          </form>
        </div>
      )

}


export default Register;
