      const [password, setUserPassword] = React.useState('');
      const token = Cookies.get('token');
      const [data, SetData] = useState('')


    const AllProfilsFetch = () => {

      fetch('http://localhost:1337/users', {
          method: "GET",
          headers: {
          "Authorization":`Bearer ${token}`,
