      "Authorization":`Bearer ${token}`,
      "Content-Type": "application/json"
      },
    })
    .then((response) => response.json())
    .then((data) => {
        SetData(data)
        console.log(data)
      }
    )
  }

  useEffect(() => {
      profileFetch();
